package io.swagger.repository;import io.swagger.model.DailyActivities;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.CrudRepository;import org.springframework.stereotype.Repository;import java.time.LocalDate;import java.util.List;@Repositorypublic interface DailyActivitiesRepository extends CrudRepository<DailyActivities,Long> {    @Query(value = "SELECT * FROM daily_activities WHERE date BETWEEN ? AND ?",nativeQuery = true)    List<DailyActivities> getMonthBazarList(LocalDate start,LocalDate end);    @Query(value = "SELECT sum(total_cost) FROM daily_activities WHERE date BETWEEN ? AND ?",nativeQuery = true)    double getSumOfMonthBazarList(LocalDate start,LocalDate end);    @Query(value = "SELECT * FROM daily_activities WHERE date BETWEEN ? AND ? AND username=?",nativeQuery = true)    List<DailyActivities> getUserMonthBazarList(LocalDate start,LocalDate end,String username);}