package io.swagger.repository;import io.swagger.model.DepositInformation;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.CrudRepository;import org.springframework.stereotype.Repository;import java.time.LocalDate;import java.util.List;@Repositorypublic interface DepositRepository extends CrudRepository<DepositInformation,Long> {    @Query(value = "SELECT * FROM deposit_information WHERE local_date BETWEEN ? AND ?",nativeQuery = true)    List<DepositInformation> getMonthDepositList(LocalDate start, LocalDate end);    @Query(value = "SELECT SUM(amount) FROM deposit_information WHERE local_date BETWEEN ? AND ?",nativeQuery = true)    double getSumOfMonthDepositList(LocalDate start, LocalDate end);    @Query(value = "SELECT * FROM deposit_information WHERE local_date BETWEEN ? AND ? AND username=?",nativeQuery = true)    List<DepositInformation> getMonthDepositOfMember(LocalDate start, LocalDate end,String username);    @Query(value = "SELECT SUM(amount) FROM deposit_information WHERE local_date BETWEEN ? AND ? AND username=?",nativeQuery = true)    double getSumOfMonthDepositOfMember(LocalDate start, LocalDate end,String username);}