package io.swagger.repository;import io.swagger.model.Evaluation;import io.swagger.model.LoginInformation;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.CrudRepository;import org.springframework.stereotype.Repository;import java.time.LocalDate;import java.util.List;@Repositorypublic interface EvaluationRepository extends CrudRepository<Evaluation,Long> {    @Query(value = "SELECT ((AVG(ablity)+AVG(relation_with_member)+AVG(food_quality))/3) FROM evaluation WHERE manager_user_name=? AND date BETWEEN ? AND ?",nativeQuery = true)    double getAvgEvaluationForManager(String managerUserName,LocalDate start,LocalDate end);    @Query(value = "SELECT * FROM evaluation WHERE date BETWEEN ? AND ? AND manager_user_name=?",nativeQuery = true)    List<Evaluation> getManagerMonthEvaluation(LocalDate start, LocalDate end, String managerUserName);}