package io.swagger.repository;import io.swagger.model.Meal;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.CrudRepository;import org.springframework.stereotype.Repository;import java.time.LocalDate;import java.util.List;@Repositorypublic interface MealRepository extends CrudRepository<Meal,Long> {    @Query(value = "SELECT * FROM meal WHERE username=? AND date=?",nativeQuery = true)    List<Meal> getMeal(String username, LocalDate date);    @Query(value = "SELECT * FROM meal WHERE date BETWEEN ? AND ?",nativeQuery = true)    List<Meal> getMonthMeal(LocalDate start,LocalDate end);    @Query(value = "SELECT (SUm(dinner)+SUM(lunch)+SUM(breakfast)) FROM meal WHERE date BETWEEN ? AND ?",nativeQuery = true)    double getSumOfMonthMeal(LocalDate start, LocalDate end);    @Query(value = "SELECT * FROM meal WHERE date BETWEEN ? AND ? AND username=?",nativeQuery = true)    List<Meal> getUserMonthMeal(LocalDate start,LocalDate end,String username);    @Query(value = "SELECT (SUm(dinner)+SUM(lunch)+SUM(breakfast)) FROM meal WHERE date BETWEEN ? AND ? AND username=?",nativeQuery = true)    double getSumOfUserMonthMeal(LocalDate start, LocalDate end,String username);}